import React, { useState } from 'react'

const Add = () => {
    const [query, setQuery] = useState('')

    const onChange = (e) => {
        e.preventDefault()

        setQuery(e.target.value)
        fetch(`https://api.themoviedb.org/3/search/movie?api_key=9849f72d8aa0fd9676c39fbb4a5c5bd1&query=${e.target.value}`)
        .then(res => res.json())
        .then(data => {
            if(!data.errors) {
                console.log(data.results)
            }
        })

    }

    return (
        <div className="add-page">
            <div className="container">
                <div className="add-content">
                    <div className="input-wrapper">
                        <input type="text" 
                        placeholder="Search for a movie"
                        value={query}
                        onChange={onChange}
                        />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Add
